import React from 'react';
import '../styles/styleShift.css';
import picUser1 from '../pic/user1.png';
import picUser2 from '../pic/user2.png';
import picUser3 from '../pic/user3.png';
import picUser4 from '../pic/user4.png';
import picUser5 from '../pic/user5.png';
import UserTable from './userTable';
import Navbar from './navbar';
import ExcelJS from 'exceljs';
import { saveAs } from 'file-saver';

function Shift() {

    const handleDownload = async () => {
        const workbook = new ExcelJS.Workbook();
        const worksheet = workbook.addWorksheet('Next Week Schedule');
    
        // Add header row
        const headerRow = worksheet.addRow(['Date', 'beginning time', 'end time', 'skill', 'demand']);
    
        // Calculate the dates for the next week starting from Sunday
        const today = new Date();
        const nextSunday = new Date(today.setDate(today.getDate() + (7 - today.getDay())));
        const dates = Array.from({ length: 7 }, (_, i) => {
          const date = new Date(nextSunday);
          date.setDate(nextSunday.getDate() + i);
          return date.toLocaleDateString('en-GB'); // Format: dd/mm/yyyy
        });
    
        // Add date rows
        dates.forEach(date => {
          worksheet.addRow([date, '', '', '', '']);
        });
    


        // Define common font style
        const commonFont = { name: 'Assistant', size: 12 };


        // Style the header
        headerRow.eachCell(cell => {
          cell.fill = {
            type: 'pattern',
            pattern: 'solid',
            fgColor: { argb: '854a51' }
          };
          cell.font = { ...commonFont, color: { argb: 'FFFFFF' }, bold: true };
          cell.alignment = { vertical: 'middle', horizontal: 'center' };
          cell.border = {
            top: { style: 'thin' },
            left: { style: 'thin' },
            bottom: { style: 'thin' },
            right: { style: 'thin' },
          };
        });
    
        // Style the data rows
        worksheet.eachRow((row, rowNumber) => {
          if (rowNumber !== 1) {
            row.eachCell(cell => {
                
                cell.font = { ...commonFont, color: { argb: 'FFFFFF' }, bold: true };

              cell.border = {
                top: { style: 'thin' },
                left: { style: 'thin' },
                bottom: { style: 'thin' },
                right: { style: 'thin' },
              };
            });
          }
        });
    
        // Auto size columns
        worksheet.columns.forEach(column => {
          let maxLength = 0;
          column.eachCell({ includeEmpty: true }, cell => {
            const columnLength = cell.value ? cell.value.toString().length : 10;
            if (columnLength > maxLength) {
              maxLength = columnLength;
            }
          });
          column.width = maxLength < 10 ? 10 : maxLength;
        });
















