import React from 'react';
import '../styles/styleShift.css';
import picUser1 from '../pic/user1.png';
import picUser2 from '../pic/user2.png';
import picUser3 from '../pic/user3.png';
import picUser4 from '../pic/user4.png';
import picUser5 from '../pic/user5.png';
import UserTable from './userTable';
import Navbar from './navbar';
import ExcelJS from 'exceljs';
import { saveAs } from 'file-saver';

function Shift() {
  const handleDownload = async () => {
    const currentDate = new Date();
    const year = currentDate.getFullYear();
    const month = currentDate.getMonth();
    const lastDay = new Date(year, month + 1, 0).getDate();

    const workbook = new ExcelJS.Workbook();
    const worksheet = workbook.addWorksheet('Shift Schedule');

    // Add header row
    const headerRow = worksheet.addRow(['Shift', 'Position']);
    for (let day = 1; day <= lastDay; day++) {
      const date = new Date(year, month, day);
      const dayName = date.toLocaleDateString('en-US', { weekday: 'long' });
      headerRow.getCell(day + 2).value = `${day}.${month + 1}.${year}\n${dayName}`;
    }

    // Add data rows
    const shifts = [
      ['07:00-11:30', 'Service and Info', 'Badan John', '', '', 'Badan John', '', 'Badan John'],
      ['07:00-11:30', 'Service and Info', '', 'Badan John', 'Badan John', 'Badan John', '', ''],
      ['07:00-11:30', 'Service and Info', '', 'Badan John', '', '', 'Badan John', ''],
    ];

    shifts.forEach(shift => {
      worksheet.addRow(shift);
    });

    // Style the header
    headerRow.eachCell(cell => {
      cell.fill = {
        type: 'pattern',
        pattern: 'solid',
        fgColor: { argb: '800000' }
      };
      cell.font = { color: { argb: 'FFFFFF' }, bold: true };
      cell.alignment = { vertical: 'middle', horizontal: 'center', wrapText: true };
      cell.border = {
        top: { style: 'thin' },
        left: { style: 'thin' },
        bottom: { style: 'thin' },
        right: { style: 'thin' },
      };
    });

    // Style the data rows
    worksheet.eachRow((row, rowNumber) => {
      if (rowNumber !== 1) {
        row.eachCell(cell => {
          cell.border = {
            top: { style: 'thin' },
            left: { style: 'thin' },
            bottom: { style: 'thin' },
            right: { style: 'thin' },
          };
        });
      }
    });

    // Auto size columns
    worksheet.columns.forEach(column => {
      let maxLength = 0;
      column.eachCell({ includeEmpty: true }, cell => {
        const columnLength = cell.value ? cell.value.toString().length : 10;
        if (columnLength > maxLength) {
          maxLength = columnLength;
        }
      });
      column.width = maxLength < 10 ? 10 : maxLength;
    });

    // Save the file
    const buffer = await workbook.xlsx.writeBuffer();
    const blob = new Blob([buffer], { type: 'application/octet-stream' });
    saveAs(blob, 'Shift_Schedule.xlsx');
  };

  return (
    <div className="container">
      <Navbar />

      <section className="main">
        <div className="center-header">
          <h1 className="header">- Upload Files -</h1>
          <button onClick={handleDownload}>Download Excel</button>
        </div>

        <section className="attendance">
          <div className="attendance-list">
            <table className="table">
              <thead>
                <tr>
                  <th>Shift</th>
                  <th>Position</th>
                  <th>
                    <div className="td-day">
                      <h1 className="Font-big">12</h1>
                      <h4 className="Font-small">Sunday</h4>
                    </div>
                  </th>
                  <th>
                    <div className="td-day">
                      <h1 className="Font-big">13</h1>
                      <h4 className="Font-small">Monday</h4>
                    </div>
                  </th>
                  <th>
                    <div className="td-day">
                      <h1 className="Font-big">14</h1>
                      <h4 className="Font-small">Tuesday</h4>
                    </div>
                  </th>
                  <th>
                    <div className="td-day">
                      <h1 className="Font-big">15</h1>
                      <h4 className="Font-small">Thursday</h4>
                    </div>
                  </th>
                  <th>
                    <div className="td-day">
                      <h1 className="Font-big">16</h1>
                      <h4 className="Font-small">Friday</h4>
                    </div>
                  </th>
                  <th>
                    <div className="td-day">
                      <h1 className="Font-big">17</h1>
                      <h4 className="Font-small">Saturday</h4>
                    </div>
                  </th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>07:00-11:30</td>
                  <td>Service and Info</td>
                  <td>
                    <UserTable name="Badan John" picture={picUser1} />
                  </td>
                  <td></td>
                  <td></td>
                  <td>
                    <UserTable name="Badan John" picture={picUser2} />
                  </td>
                  <td></td>
                  <td>
                    <UserTable name="Badan John" picture={picUser3} />
                  </td>
                </tr>
                <tr>
                  <td>07:00-11:30</td>
                  <td>Service and Info</td>
                  <td></td>
                  <td>
                    <UserTable name="Badan John" picture={picUser1} />
                  </td>
                  <td>
                    <UserTable name="Badan John" picture={picUser2} />
                  </td>
                  <td>
                    <UserTable name="Badan John" picture={picUser4} />
                  </td>
                  <td></td>
                  <td></td>
                </tr>
                <tr>
                  <td>07:00-11:30</td>
                  <td>Service and Info</td>
                  <td></td>
                  <td>
                    <UserTable name="Badan John" picture={picUser5} />
                  </td>
                  <td></td>
                  <td></td>
                  <td>
                    <UserTable name="Badan John" picture={picUser4} />
                  </td>
                  <td></td>
                </tr>
              </tbody>
            </table>
          </div>
        </section>
      </section>
    </div>
  );
}

export default Shift;
