// src/components/ProfilePicUploader.js

import React, { useState } from 'react';


function ProfilePic() {
  const [photo, setPhoto] = useState('./pic/picProfile.png');

  const handleFileChange = (e) => {
    const choosedFile = e.target.files[0];
    if (choosedFile) {
      const reader = new FileReader();
      reader.addEventListener('load', () => {
        setPhoto(reader.result);
      });
      reader.readAsDataURL(choosedFile);
    }
  };

  return (
    <div className="profile-pic-div">
      <img src={photo} id="photo" alt="Profile" />
      <input type="file" id="file" onChange={handleFileChange} />
      <label htmlFor="file" id="uploadBtn">Choose Photo</label>
    </div>
  );
}

export default ProfilePic;



