import React, { useState, useRef } from 'react';
import './styleLogin.css';

const ProfilePic = () => {
  const [image, setImage] = useState('./pic/picProfile.png');
  const fileInputRef = useRef(null);
  const uploadBtnRef = useRef(null);

  const handleMouseEnter = () => {
    if (uploadBtnRef.current) {
      uploadBtnRef.current.style.display = 'block';
    }
  };

  const handleMouseLeave = () => {
    if (uploadBtnRef.current) {
      uploadBtnRef.current.style.display = 'none';
    }
  };

  const handleFileChange = (event) => {
    const choosedFile = event.target.files[0];

    if (choosedFile) {
      const reader = new FileReader();
      reader.onload = () => {
        setImage(reader.result);
      };
      reader.readAsDataURL(choosedFile);
    }
  };

  return (
    <div 
      className="profile-pic-div" 
      onMouseEnter={handleMouseEnter} 
      onMouseLeave={handleMouseLeave}
    >
      <img src={image} alt="Profile" id="photo" />
      <input 
        type="file" 
        id="file" 
        ref={fileInputRef} 
        onChange={handleFileChange} 
        style={{ display: 'none' }} 
      />
      <label 
        htmlFor="file" 
        id="uploadBtn" 
        ref={uploadBtnRef} 
        style={{ display: 'none' }}
      >
        Choose Photo
      </label>
    </div>
  );
};

export default ProfilePic;
